command:
python -m pipelines.rdkit.cluster_butina -i ${PIN}input.data.gz -if json
 -o ${POUT}output -of json -t ${threshold} -d ${descriptor} -m ${metric} --num ${count}
 --exclude ${diversity} --field optimise ${maximise ? '--max'' : '--min'}
${binding.variables.containsKey('fragment_method') ? ' --fragment-method ' + fragment_method : ''} ${binding.variables.containsKey('output_fragment') && output_fragment ? ' --output-fragment' : ''} --thin --meta

nextflowParams:
params.ligands = 'ligands.data.gz'
params.receptor = 'config.zip'
params.num_dockings = $num
params.top = $top
${binding.variables.containsKey('score') ? 'params.score = ' + score : ''}
${binding.variables.containsKey('nscore') ? 'params.nscore = ' + nscore : ''}
